mapper.update:
UPDATE board
SET b_title = #{b_title}, b_content = #{b_content}
WHERE b_no = #{b_no}

UPDATE board
SET b_views = b_views + 1
WHERE b_no = #{b_no}

mapper.select:
SELECT * FROM board WHERE b_no > 0
AND ${type} LIKE CONCAT('%',#{keyword},'%')
ORDER BY b_no DESC LIMIT #{start}, #{amount}
